mapper.select:
SELECT temp.uri AS media_uri, temp.filename AS media_filename, temp.sizebytes AS media_sizebytes, temp.creation AS media_creation, temp.modified AS media_modified, temp.version AS media_version, mediatype.type AS type_name, mediatag.tag AS tag_name FROM (SELECT media.* from media INNER JOIN mediatag ON mediatag.uri = media.uri WHERE
mediatag.tag = #{item}
GROUP BY media.uri HAVING COUNT(*) = #{size}) as temp INNER JOIN mediatag ON mediatag.uri = temp.uri INNER JOIN mediatype ON mediatype.uri = temp.uri WHERE

 

	
		media.filename LIKE '%' #{query} '%'
	
	
		AND media.creation > #{fromTimestamp}
	
	
		AND media.creation < #{toTimestamp}
	
	
		AND media.version > #{minVersion}
		
	
		AND media.version < #{maxVersion}
		
 			
AND
